import React from 'react'
import { StyleSheet, View, TouchableHighlight, FlatList, Text } from 'react-native'
import { Button, ListItem } from 'react-native-elements';
import MovieFragment from './MovieFragment';

export default class HomeScreen extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            movies: []
        }
    }

    addMovie = (movie) => {
        this.setState(prevState => ({
            movies: [...prevState.movies, movie]
        }))
    }

    keyExtractor = (item, index) => index.toString()

    movieDetails = (movie) => {
        this.props.navigation.navigate('MovieDetails', {
            movie: movie
        })
    }

    renderItem = ({ item }) => (
        <MovieFragment movie={item} movieDetails={() => this.movieDetails(item)} />
    )

    render() {
        return (
            <View style={styles.container}>
                <Text style={styles.title}>Ma liste de films</Text>
                <FlatList
                    keyExtractor={this.keyExtractor}
                    data={this.state.movies}
                    renderItem={this.renderItem}
                />
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        color: 'white',
        backgroundColor: 'black',
        alignItems: 'center',
        justifyContent: 'center',
    },
    title:{
        marginLeft: 20,
        marginTop: 10,
        marginBottom: 0,
        fontWeight: 'bold',
        fontSize: 30,
        color:'white'
    },
    addButton: {
        position: 'absolute',
        alignItems: 'center',
        justifyContent: 'center',
        right: 30,
        bottom: 30,
    }
})
