import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { MaterialCommunityIcons } from '@expo/vector-icons';

import LoginScreen from './controllers/LoginScreen';
import HomeScreen from './controllers/HomeScreen';
import AddMovie from './controllers/AddMovie'
import MovieDetails from './controllers/MovieDetails'

//const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

export default class App extends React.Component {
	render() {
		return (
			<NavigationContainer>
				<Tab.Navigator screenOptions={{
					headerStyle: {
						backgroundColor: '#222831',
					},
					headerTintColor: '#fff',
					headerTitleStyle: {
						fontWeight: 'bold',
					},
				}}>
					<Tab.Screen name="Login" component={LoginScreen} options={{ title: 'Connexion' }} />
					<Tab.Screen name="Home" component={HomeScreen} 
                      options={{
                        tabBarLabel: 'Home',
                        tabBarIcon: ({ color, size }) => (
                          <MaterialCommunityIcons name="home" color={color} size={size} />
                        ),headerLeft: null 
                      }}/>
					<Tab.Screen name="AddMovie" component={AddMovie} options={{ title: 'Ajouter un film' }}/>
					<Tab.Screen name="MovieDetails" component={MovieDetails} options={{ title: 'Test' }}/>
				</Tab.Navigator>
			</NavigationContainer>
		)
	}
}
